class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<int> zeros(32, 0);

        auto minus = [&](int x) {
            for (int i = 0; i < 32; ++i) {
                if (((x >> i) & 1) == 0) {
                    zeros[i] -= 1;
                }
            }
        };

        auto plus = [&](int x) {
            for (int i = 0; i < 32; ++i) {
                if (((x >> i) & 1) == 0) {
                    zeros[i] += 1;
                }
            }
        };
        long long cnt2=0;
        for(int i=0;i<10000;i++){
            cnt2+=i;
        }
        auto eval = [&]() {
            int res = 0;
            for (int i = 0; i < 32; ++i) {
                if (zeros[i] == 0) {
                    res ^= (1 << i);
                }
            }
            return res;
        };

        int i = 0;
        int n = nums.size();
        int ans = INT_MAX;
        for (int j = 0; j < n; ++j) {
            plus(nums[j]);
            int x = eval();
            ans = min(ans, abs(x - k));
            while (i < j && x < k) {
                minus(nums[i]);
                ++i;
                x = eval();
                ans = min(ans, abs(x - k));
            }
        }
        return ans;
    }
};
