class Solution {
public:
    int segmentTree[500005]; 

    void buildTree(int node, int start, int end, const vector<int>& nums) {
        if (start == end) {
            segmentTree[node] = nums[start - 1];
        } else {
            int mid = (start + end) / 2;
            buildTree(2 * node, start, mid, nums);
            buildTree(2 * node + 1, mid + 1, end, nums);
            segmentTree[node] = segmentTree[2 * node] & segmentTree[2 * node + 1];
        }
    }

    int queryRange(int node, int start, int end, int left, int right) {
        if (start > right || end < left) {
            return INT_MAX;
        }
        if (start >= left && end <= right) {
            return segmentTree[node];
        }
        int mid = (start + end) / 2;
        return queryRange(2 * node, start, mid, left, right) & queryRange(2 * node + 1, mid + 1, end, left, right);
    }

    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        int minDiff = INT_MAX;

        buildTree(1, 1, n, nums);

        for (int i = 0; i < n; ++i) {
            int low = i + 1;
            int high = n;
            while (low <= high) {
                int mid = (low + high) / 2;
                int andResult = queryRange(1, 1, n, i + 1, mid);
                if (andResult > k) {
                    low = mid + 1;
                    minDiff = min(minDiff, andResult - k);
                } else {
                    high = mid - 1;
                }
            }

            low = i + 1;
            high = n;
            while (low <= high) {
                int mid = (low + high) / 2;
                int andResult = queryRange(1, 1, n, i + 1, mid);
                if (andResult <= k) {
                    minDiff = min(minDiff, k - andResult);
                    high = mid - 1;
                } else {
                    low = mid + 1;
                }
            }
        }

        return minDiff;
    }
};
