class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        const int n = nums.size();

        int best = 1e9;
        vector<int> freq(32,0);
        auto update = [&freq](int x) {
            for (int ii = 0; ii < 32; ++ii) {
                if (!(x&1)) freq[ii]++;
                x >>= 1;
            }
        };

        auto dec = [&freq] (int x) {
            for (int ii = 0; ii < 32; ++ii) {
                if (!(x&1)) freq[ii]--;
                x >>= 1;
            }
        };


        auto get = [&freq]() {
            int x = 0;
            for (int ii = 31; ii >= 0; --ii) {
                x <<= 1;
                // printf("%d ", freq[ii]);
                if (freq[ii] == 0) x |= 1;
            }
            // printf("\n");
            return x;
        };

        for (int l = 0, r = 0; r < n; ++r) {
            update(nums[r]);

            int x = get();
            // printf("%d %d: %d %d\n", l, r, x, k);
            best = min(best, abs(k-x));
            while (x < k && l < r) {
                dec(nums[l]);
                l++;
                x = get();
                best = min(best, abs(k-x));
            }
            // printf("%d %d: %d %d\n", l, r, x, k);
            best = min(best, abs(k-x));
        }
        return best;
    }
};
