class Solution {
public:

    int sz = 31;
    vector<vector<int>> bits;

    int getNum(int st, int end) {
        vector<int> temp = bits[end];

        if(st-1 >= 0)
            for(int j = 0; j < sz; j++)
                temp[j] -= bits[st-1][j];

        int len = end - st + 1;
        int num = 0;

        for(int i = 0; i < sz; i++) {
            int val = temp[i];
            if(val == len) num |= (1 << i);
        }

        return num;
    }

    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        bits = vector<vector<int>>(n, vector<int>(sz));

        for(int i = 0; i < n; i++) {
            int curr = nums[i];
            for(int j = 0; j < sz; j++) {
                if((curr >> j) & 1) 
                    bits[i][j] = 1;
            }
        }

        for(int i = 1; i < n; i++)
            for(int j = 0; j < sz; j++)
                bits[i][j] += bits[i-1][j];

        int res = INT_MAX;

        int j = 0;
        for(int i = 0; i < n; i++) {
            while(getNum(j, i) < k) {
                j++;
            }
            // j - i
            int num = getNum(j, i);
            // j-1 - i
            int num2 = getNum(max(0, j-1), i);

            res = min(res, abs(num - k));
            res = min(res, abs(num2 - k));
        }

        return res;
    }
};
