class Solution {
    public int minimumDifference(int[] nums, int k) {
        Set<Integer> set =new HashSet();
        set.add(nums[0]);
        int res=Math.abs(k-nums[0]);
        for(int i=1;i<nums.length;i++){
            Set<Integer> tmp =new HashSet();
            tmp.add(nums[i]);
            res=Math.min(res,Math.abs(nums[i]-k));
            for(int j:set){
                int num=j & nums[i];
                res=Math.min(res,Math.abs(num-k));
                if(num>k){
                    tmp.add(num);
                }
            }
            set=tmp;
        }
        return res;
    }
}
