#define INF INT32_MAX 
#define ll long long int
class SegmentTree { 
public: 
    vector<int>seg_tree; 
    int n; 
    SegmentTree(vector<int>& arr){
        n = arr.size(); 
        seg_tree.resize(4 * n); 
        bnao(arr, 0, 0, n - 1); 
    } 

    void bnao(vector<int>& arr, int x, int low, int high){ 
        if(low==high){ 
            seg_tree[x] = arr[low]; 
        } 
        else{ 
            int mid=low+(high-low)/2;
            bnao(arr,2*x+1,low,mid); 
            bnao(arr,2*x+1+1,mid+1,high); 
            seg_tree[x] = seg_tree[2*x + 1] & seg_tree[2*x + 2]; 
        } 
    } 
    int query(int x, int low,int high,int a,int b){ 
        if(a > high || low > b){
            return INF;
        }
        if(low>=a && b>=high){
            return seg_tree[x];
        }
        int mid=low+(high-low)/2;
        int q1=query(2*x+1,low,mid,a,b); 
        int q2=query(2*x+1+1,mid+1,high,a,b); 
        int res=q1 & q2;
        return res;
    } 
    int query(int a,int b){ 
        return query(0,0,n-1,a,b);
    } 

}; 

int solve(vector<int> arr, int k){ 
    int n=arr.size(); 
    SegmentTree st(arr); 
    ll ans=INF;
    for(int i=0;i<n;i++){ 
        ll low=i-1;;
        ll high=n-1;
        while(high-low>1){ 
            ll mid=low+(high-low)/2;
            if(st.query(i,mid)==k){
                low=mid+1;
                low--;
        }
            else if(st.query(i,mid)<k){
                high=mid-1;
                high++;
    }
            else if(st.query(i,mid)>k){
                low=mid+1;
                low--;
            }
        } 
        if(low!=i - 1){
            ll diff=abs(st.query(i,low)-k);
            ans=min(diff,ans);
        }
        ll sub=abs(k-st.query(i,high));
        ans=min(sub,ans);
    } 
    return ans; 
}

class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        return solve(nums, k); 
    }
};
