class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int ans=INT_MAX;

        for(int i=31;i>=0;i--)
        {
            int x=(1<<i);
            int ss=-1;
            int r=nums[0];
             for(int j=0;j<nums.size();j++)
             { r=r&nums[j];
                 if(x & nums[j])
                 {
                     if(ss==-1)
                     {
                         ss=nums[j];
                     }
                     else ss=(ss&nums[j]);
                     ans=min(abs(k-ss),ans);
                 }
                 else{
                     ss=-1;
                 }
             }
            ans=min(abs(r-k),ans);


        }

        return ans;
    }
};
