class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        ll left = 0, res = INT32_MAX;
        int cnt[32] = {0};
        for (int i = 0; i < nums.size(); i++) {
            ll a = nums[i], num = 0;
            for (int j = 0; j < 31; j++, a >>= 1) {
                if (a & 1) cnt[j]++;
                if (cnt[j] == i - left + 1) {
                    num += 1ll << j;
                }
            }
            res = min(res, abs(num - k));
            while (num < k && left <= i) {
                ll newNum = 0;
                a = nums[left];
                for (int j = 0; j < 31; j++, a >>= 1) {
                    if (a & 1) cnt[j]--;
                    if (cnt[j] == i - left) {
                        newNum += 1ll << j;
                    }
                }
                res = min(res, abs(newNum - k));
                left++;
                num = newNum;
            }
        }
        return res;
    }
};
