class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int>> arr(n,vector<int>(26,0));
        vector<int> v(32,0);
        for(int i=0;i<n;i++){
            for(int j=0;j<31;j++){
                int mask=1<<j;
                if(mask&nums[i]) v[j]++;
            }
            arr[i]=v;
        }
        int ans=INT_MAX;
        for(int i=0;i<n;i++){
            int l=i,h=n-1;
            while(l<=h){
                int m=l+(h-l)/2;
                int val=0;
                for(int j=0;j<31;j++){
                    int x=arr[m][j];
                    if(i>0) x-=arr[i-1][j];
                    if(x==m-i+1) val|=1<<j;
                }                
                ans=min(ans,abs(val-k));
                if(val>=k){
                    l=m+1;
                }
                else{
                    h=m-1;
                }
            }
        }
        return ans;
    }
};
