#define INF INT32_MAX
class Solution {
public:

    class SegmentTree {
public:
    vector<int> t;
    int size;

    SegmentTree(vector<int>& arr)
    {
        size = arr.size();
        t.resize(4 * size);
        build(arr, 0, 0, size - 1);
    }
    void build(vector<int>& arr, int node, int start,
               int end)
    {
        if (start == end) {
            t[node] = arr[start];
        }
        else {
            int mid = (start + end) / 2;
            build(arr, 2 * node + 1, start, mid);
            build(arr, 2 * node + 2, mid + 1, end);

            t[node] = t[2 * node + 1] & t[2 * node + 2];
        }
    }

    int query(int l, int r)
    {
        return query(0, 0, size - 1, l, r);
    }

    int query(int node, int start, int end, int l, int r)
    {
        if (r < start || end < l) {
            return INF;
        }
        if (l <= start && end <= r) {
            return t[node];
        }
        int mid = (start + end) / 2;
        int p1 = query(2 * node + 1, start, mid, l, r);
        int p2 = query(2 * node + 2, mid + 1, end, l, r);

        return p1 & p2;
    }
};
    int minimumDifference(vector<int>& nums, int k) {
         int n = nums.size();
    SegmentTree st(nums);
    int res = INF;
    for (int i = 0; i < n; i++) {
        int left = i - 1, right = n - 1, mid;

        while (right - left > 1) {
            mid = (left + right) / 2;
            if (st.query(i, mid) >= k)
                left = mid;
            else
                right = mid;
        }
        if (left != i - 1)
            res = min(res, abs(st.query(i, left) - k));
        res = min(res, abs(k - st.query(i, right)));
    }
    return res;
    }
};
