class SegmentTree {
    vector<int> tree;
    int n;

public:
    SegmentTree(int n, vector<int> &v) {
        this->n = n;
        tree.resize(4 * n);
        build(v, 0, n - 1, 0);
    }

    void build(vector<int> &v, int start, int end, int node) {
        if (start == end) {
            tree[node] = v[start];
            return;
        }
        int mid = (start + end) / 2;
        build(v, start, mid, 2 * node + 1);
        build(v, mid + 1, end, 2 * node + 2);
        tree[node] = merge(tree[2 * node + 1], tree[2 * node + 2]);
    }

    int merge(int node1, int node2) {
        return node1 & node2;
    }

    int query(int left, int right) {
        return Query(left, right, 0, n - 1, 0);
    }

    int Query(int left, int right, int start, int end, int node) {
        if (left <= start && right >= end) {
            return tree[node];
        }
        if (left > end || right < start) {
            return INT_MAX; 
        }
        int mid = (start + end) / 2;
        int ans1 = Query(left, right, start, mid, 2 * node + 1);
        int ans2 = Query(left, right, mid + 1, end, 2 * node + 2);
        return merge(ans1, ans2);
    }
};
class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        int ans = INT_MAX;
        SegmentTree st(n, nums);

        for (int i = 0; i < n; ++i) {
            int low = i, high = n - 1;

            while (low <= high) {
                int mid = (low + high) / 2;
                int andResult = st.query(i, mid);
                int currentDiff = abs(k - andResult);

                ans = min(ans, currentDiff);

                if (andResult < k) {
                    high = mid - 1;
                } else {
                    low = mid + 1;
                }
            }
        }
        return ans;
    }
};
