class Solution {
public:
    int minimumDifference(vector<int> &a, int k) {
        int n=a.size();
        vector<vector<int>> pref(n,vector<int>(30));
        for(int i=0;i<n;i++){
            for(int j=0;j<30;j++){
                if((a[i]&(1<<j))==0){
                    pref[i][j]=1;
                }
            }
        }
        for(int i=1;i<n;i++){
            for(int j=0;j<30;j++){
                pref[i][j]+=pref[i-1][j];
            }
        }
        int res=abs(a[0]-k);
        for(int i=1;i<n;i++){
            res=min(res,abs(a[i]-k));
            int l0=0,r0=i-1;
            while(l0<=r0){
                int m0=l0+(r0-l0)/2;
                int value=0;
                for(int j=0;j<30;j++){
                    if(m0==0){
                        if(pref[i][j]==0) value+=(1<<j);
                        continue;
                    }
                    if(pref[i][j]==pref[m0-1][j]){
                        value+=(1<<j);
                        continue;
                    }
                }
                if(value<=k){
                    res=min(res,abs(k-value));
                    l0=m0+1;
                }
                else{
                    r0=m0-1;
                }
            }
            int l1=0,r1=i-1;
            while(l1<=r1){
                int m1=l1+(r1-l1)/2;
                int value=0;
                for(int j=0;j<30;j++){
                    if(m1==0){
                        if(pref[i][j]==0) value+=(1<<j);
                        continue;
                    }
                    if(pref[i][j]==pref[m1-1][j]){
                        value+=(1<<j);
                        continue;
                    }
                }
                if(value>=k){
                    res=min(res,abs(k-value));
                    r1=m1-1;
                }
                else{
                    l1=m1+1;
                }
            }
        }
        return res;
    }
};
