class Solution {
public:
    int helper(int left,int right,vector<vector<int>> &prefix){
        if(left<0 || right<0) return 1e9;
        int ans=0;
        int len = right-left+1;
        for(int i=0;i<32;i++){
            int temp = prefix[i][right];
            if(left!=0) temp-=prefix[i][left-1];
            if(temp==len) ans|=(1<<i);
        }
        return ans;
    }
    int minimumDifference(vector<int>& nums, int k) {
        // 1101 
        vector<vector<int>> prefix(32,vector<int>(nums.size(),0));
        for(int i=0;i<32;i++){
            prefix[i][0]=(1&(nums[0]>>i));
            for(int j=1;j<nums.size();j++){
                prefix[i][j]=prefix[i][j-1]+(1&(nums[j]>>i));
            }
        }
        int ans = 1e9;

        // cout<<query(1,2,prefix);

        for(int i=0;i<nums.size();i++){
            int low=i;
            int high = nums.size()-1;
            while((high-low)>1){
                int mid = (low+high)/2;
                // ttttfffff
                if(helper(i,mid,prefix)>=k) low=mid;
                else high=mid;
            }
            ans=min({ans,abs(k-helper(i,high,prefix)),abs(k-helper(i,low,prefix)),abs(k-nums[i])});
        }

        return ans;

