class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        set<int> s;
        int ans = abs(nums[0]-k);
        for(int i=0;i<n;i++){
            set<int> ns;
            ns.insert(nums[i]);
            for(auto it : s){
                ns.insert(nums[i]&(it));
            }
            for(auto it : ns){
                ans = min(ans,abs(k-it));
            }
            s=ns;
        }
        return ans;
    }
};
