class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        int ans = INT_MAX;
        vector<int> z(32, 0);
        auto mn = [&](int a) {
            for (int i = 0; i < 32; ++i) 
            {
                if (((a >> i) & 1) == 0) 
                    --z[i];
            }
        };

        auto pl = [&](int a) {
            for (int i = 0; i < 32; ++i) 
            {
                if (((a >> i) & 1) == 0) 
                    ++z[i];
            }
        };

        auto ev = [&]() {
            int xr = 0;
            for (int i = 0; i < 32; ++i) 
            {
                if (!z[i]) 
                    xr ^= (1 << i);
            }
            return xr;
        };

        int i = 0;
        for (int j = 0; j < n; ++j) 
        {
            pl(nums[j]);
            int e=ev();
            ans = min(ans,abs(e- k));
            while (i < j && e < k) 
            {
                mn(nums[i]);
                ++i;
                e = ev();
                ans = min(ans, abs(e - k));
            }
        }

        return ans;
    }
};
