class Solution {
public:
    // [1,2,4,5] 3
    // 1 3
    // 2 3
    // 4 3
    // 4 5 3
    // [9,10,7] 6
    // 1001
    // 1010
    //  111
    // [42,49,95,76,66]
    // 12
    // 42  101010
    // 49  110001
    // 95 1011111   95-64=31
    // 76 1001101
    // 66 1000010
    // 12.   1100
    int minimumDifference(vector<int>& nums, int k) {
        int and_n = nums[0], s = 0;
        int res = abs(and_n - k);
        for(int i=1;i<nums.size();++i) {
            //cout<<"and_n: "<<and_n<<endl;
            and_n &= nums[i];
            if (and_n < k) {
                and_n = nums[i];
                int j=i-1;
                for(;j>=s && and_n > k;--j) {
                    res = min(res, abs(and_n-k));
                    and_n &= nums[j];
                }
                s=j;
            } 
            res = min(res, abs(and_n - k));
            res = min(res, abs(nums[i]-k));
        }

        return res;
    }
};
