class Solution {
public:
    int get_and(int l, int r,vector<vector<int>>&v)
    {
        int sz=r-l+1;
        int cur=0;
        for(int j=0;j<32;j++)
        {
            int bits=v[r][j]-((l)?v[l-1][j]:0);
            if(bits==sz)
            {
                cur+=1<<j;
            }
        }

        return cur;
    }


    int minimumDifference(vector<int>& nums, int k) {

        int n=nums.size();
        vector<vector<int>>v(n,vector<int>(32));


        for(int i=0;i<n;i++)
        {
            int cur=nums[i];
            int j=0;
            while(cur)
            {
                v[i][j]=cur&1;
                cur/=2;
                j++;
            }
        }

        for(int i=1;i<n;i++)
        {
            for(int j=0;j<32;j++)
            {
                v[i][j]+=v[i-1][j];
            }
        }


        // for(int i=0;i<n;i++)
        // {
        //     for(int j=0;j<32;j++)
        //     {
        //         cout<<v[i][j]<<" ";
        //     }
        //     cout<<endl;
        // }
        int ans=1e9;
        int l=0;
        int r=0;

        while(r<n)
        {
            int cur=get_and(l,r,v);
            // cout<<l<<" "<<r<<" "<<cur<<endl;
             if(cur<k && l<r)
             {
                 l++;
             }
             else
             {
                 r++;
             }
            ans=min(ans,abs(cur-k));
        }

        l=0;
        r=0;
        while(r<n)
        {
            int cur=get_and(l,r,v);
            // cout<<l<<" "<<r<<" "<<cur<<endl;
             if(cur<k||l==r)
             {
                 r++;
             }
             else
             {
                 l++;
             }
            ans=min(ans,abs(cur-k));
        }


        return ans;

    }
};
