    class sparse{
    public:
        vector<vector<int>> st;
        int n;
        sparse(int n,int *a){
            this->n=n;
            st.resize(n+1);
            for(int i=0;i<=n;++i){
                st[i].resize(30);
            }
            for(int i=0;i<n;++i){
                st[i][0]=a[i];
            }
            for(int j=1;j<=log2(n);++j){
                for(int i=0;i+(1<<j)<=n;++i){
                    st[i][j]=(st[i][j-1]&st[i+(1<<(j-1))][j-1]);
                }
            }
        }
        int shoot(int l,int r){
            int j=log2(r-l+1);
            int ans=(st[l][j]&st[r-(1<<(j))+1][j]);
            return ans;
        }

    };


    int minimumDifference(vector<int>& nums, int k) {

        int n=sz(nums);
        int a[n];
        for(int i=0;i<n;++i){
            a[i]=nums[i];
        }
        sparse s(n,a);

        int res=2e9;
        int len=-1;
        for(int i=0;i<n;++i){

            int low=i,high=n-1,ans=i;
            while(low<=high){
                int mid=(low+high)/2;
                int val=s.shoot(i,mid);
                if(val>=k){
                    ans=mid;
                    low=mid+1;
                }
                else{
                    high=mid-1;
                }
            }

            int now=abs(s.shoot(i,ans)-k);
            res=min(res,now);

            low=i,high=n-1,ans=i;
            while(low<=high){
