class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        prev, ans = set(), inf
        for x in nums:
            curr = set([x])
            for y in prev: curr.add(x & y)
            prev = curr
            sl = sorted(curr)
            i = bisect.bisect(sl, k)
            if i > 0: ans = min(ans, k - sl[i - 1])
            if i < len(sl): ans = min(ans, sl[i] - k)
        return ans
