#define ll long long int
class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        ll n=nums.size();
        ll ans=1e18;
        set<ll> prev;
        prev.insert(nums[0]);
        ll see=abs(nums[0]-k);
        ans=min(ans,see);
        for(ll i=1;i<n;i++){
            set<ll> second;
            for(auto j:prev){
                ll xy=nums[i]&j;
                second.insert(xy);
            }

            second.insert(nums[i]);
            while(prev.size()!=0){
                ll ele=*prev.begin();
                prev.erase(ele);
            }
            for(auto j:second){
                ll see2=abs(j-k);

                ans=min(ans,see2);
                prev.insert(j);
            }
        }
        return ans;

    }
};
