class Solution {
public:
    int minimumDifference(vector<int>& numss, int k) {

       vector<int> nums;
        long long int kk=k;
        nums.push_back(numss[0]);
        long long int ans= INT_MAX;
        for(int i=1; i<numss.size(); i++)
        {
            if(numss[i]!=numss[i-1])
                nums.push_back(numss[i]);

            ans= min(ans, abs(kk-numss[i]));
        }
        ans= min(ans, abs(kk-nums[0]));

        if(ans==0)
        {
            return 0;
        }

        int n= nums.size();

        for(int i=0; i<n; i++)
        {

            if(nums[i]>k)
            {
                int ch=0;
                long long int aa= nums[i];
            for(int j=i; j<n; j++)
            {

                aa= aa& nums[j];

                ans=min(ans, abs(kk-aa));

                if(aa<=kk)
                    break;

                if(j==n-1)
                    ch=1;
            }

                if(ch==1)
                    break;
                }
        }

        int an= ans;
        return an;


    }
};
