typedef long long ll;
class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        // cout<<"e4444"<<endl;
        ll n=nums.size();
        vector<vector<ll>> pre(n, vector<ll> (32, 0));
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<32;j++)
            {
                if((nums[i]>>j) & 1ll)
                {
                    pre[i][j]++;
                }
            }
        }
        for(int i=1;i<n;i++)
        {
            for(int j=0;j<32;j++)
            {
                pre[i][j]+=pre[i-1][j];
            }
        }
        int mini= INT_MAX;
        for(int i=0;i<n;i++)
        {
            //bs
            int l=i, r=n-1;
            while(l<=r)
            {
                int mid=(l+r)/2;
                int ar=0; //and of range l to r
                for(int j=0;j<32;j++)
                {
                    int len= pre[mid][j];
                    if(i>0)
                    {
                        len-=pre[i-1][j];
                    }
                    if(len == (mid-i+1))
                    {
                        ar |= (1ll<<j);
                    }
                }
                // cout<<ar<<endl;
                if(ar >= k)
                {
                    mini= min(mini, abs(ar-k));
                    l=mid+1;
                }
                else
                {
                    r=mid-1;
                }
            }
        }
        for(int i=0;i<n;i++)
        {
            //bs
            ll l=i, r=n-1;
            while(l<=r)
            {
                int mid=(l+r)/2;
                int ar=0; //and of range l to r
                for(int j=0;j<32;j++)
                {
                    int len= pre[mid][j];
                    // cout<<j<<" "<<len<<endl;
                    if(i>0)
                    {
                        len-=pre[i-1][j];
                    }
                    if(len == (mid-i+1))
                    {
                        ar |= (1ll<<j);
                    }
                    // cout<<j<<" "<<len<<endl;
                }
                // cout<<i<<" "<<mid<<" "<<ar<<endl;
                if(ar <= k)
                {
                    mini= min(mini, abs(ar-k));
                    r=mid-1;
                    // l=mid+1;
                }
                else
                {
                    // r=mid-1;
                    l=mid+1;
                }
            }
        }
        return mini;
    }
};
