class Solution {
public:
    void build(int ind,int lo,int hi,vector<int>&a,vector<int>& seg){
        if(lo==hi){
            seg[ind]=a[lo];
            return ;
        }
        int mid=(lo+hi)>>1;
        build(2*ind+1,lo,mid,a,seg);
        build(2*ind+2,mid+1,hi,a,seg);
        seg[ind]= (seg[2*ind+1] & seg[2*ind+2]);
        return ;
    }

    int query(int ind,int lo,int hi,int l,int r,vector<int>& seg){
        //if this happen : l lo hi r
        if(l<=lo && r>=hi) return seg[ind];
        //outside the range
        if(r<lo || l>hi) return INT_MAX;
        //else
        int mid=(lo+hi)>>1;
        int left=query(2*ind+1,lo,mid,l,r,seg);
        int right=query(2*ind+2,mid+1,hi,l,r,seg);
        return left & right;
    }

    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();

        vector<int> seg(4*n);
        build(0,0,n-1,nums,seg);
        int ans = 1e9;

        for (int i = 0; i < n; i++) {

            int lo = i,hi = n-1;
            int ind = n-1;

            while(lo<=hi){
                int mid = (lo+hi)>>1;
                if(query(0,0,n-1,i,mid,seg) >= k){
                    lo = mid + 1;
                    ind = mid;
                    //ans = min(ans, abs(k - query(0,0,n-1,i,ind,seg)));
                }
                else{
                    //ans = min(ans, abs(k - query(0,0,n-1,i,mid,seg)));
                    hi = mid - 1;
                }
            }

            //cout<<ind<<" "<<nums[i]<<" "<<nums[ind]<<" "<<query(0,0,n-1,i,ind,seg)<<endl;
            ans = min(ans, abs(k - query(0,0,n-1,i,ind,seg)));
            lo = i,hi = n-1;
            while(lo<=hi){
                int mid = (lo+hi)>>1;
                if(query(0,0,n-1,i,mid,seg) <= k){
                    hi = mid - 1;
                    ind = mid;
                    //ans = min(ans, abs(k - query(0,0,n-1,i,ind,seg)));
                }
                else{
                    //ans = min(ans, abs(k - query(0,0,n-1,i,mid,seg)));
                    lo = mid + 1;
                }
            }
            ans = min(ans, abs(k - query(0,0,n-1,i,ind,seg)));
        }
        return ans;
    }
};
