class Solution {
public:
    int minimumDifference(vector<int>& nums, int kk) {

        int n = nums.size();
        int ans = 1e9;

        int l = 0, r = 0;

        vector <int> bits(33, 0);
        bool added = false;

        int cnt = 0;

        while(l < n && r < n){

            if(r < n && !added){
                for(int k = 0; k < 32; k++){
                    if( (nums[r] & (1ll << k)) )bits[k]++;
                }
                r++;
            }


            int sz = (r - l);
            // if(r == n)sz--;

            int cur = 0;
            for(int k = 0; k < 32; k++){

                if( bits[k] == sz){
                    cur |= (1ll << k);
                }
            }

            ans = min(ans, abs(kk - cur));

            // if(cur)

            // cout<<l<<" "<<r<<" "<<cur<<" "<<ans<<" ||| ";
            while(cur < kk && l < r && l < n){
                for(int k = 0; k < 32; k++){
                    if( (nums[l] & (1ll << k)) )bits[k]--;
                }

                l++;

                sz = (r - l);
                if(sz <= 0)continue;
                cur = 0;
                for(int k = 0; k < 32; k++){

                    if( bits[k] == sz){
                        cur |= (1ll << k);
                    }
                }

                ans = min(ans, abs(kk - cur));

            }

            // cout<<l<<" "<<r<<" "<<cur<<" "<<ans<<"\n";
        }

        for(int i = 0; i < n; i++){
            ans = min(ans, abs(kk - nums[i]));
        }

        return ans;
    }
};
