class Solution {
public:
    int minimumDifference(vector<int>& v, int k) {
        int n = v.size();
        vector<vector<int>> bit(n+1 , vector<int> (32 , 0));
        for(int i=0 ; i<n ; i++){
            int x = v[i];
            for(int j=0 ; j<32 ;j++){
                if(x&1){
                    bit[i+1][j] = bit[i][j] + 1;
                }else{
                    bit[i+1][j] = bit[i][j];
                }
                x/=2;
            }
        }

        long long a = v[0];
        int st =0;
        long long ans = abs( k - a);
        for(int i=1 ; i<n ; i++){
            a = a&v[i];
            // cout<<a<<endl;
            ans = min(ans , abs(a - k));
            if(a < k){
                st++;
                while(st <= i){
                    a = 0;
                    if(st == i){
                        a = v[st];
                        break;
                    }
                    // cout<<st<<" "<<i<<endl;
                    for(int j=31 ; j>=0 ;j--){
                        // cout<<bit[i+1][j]<<" * "<<bit[st][j]<<" "<<i-st + 1<<endl;
                        if(bit[i+1][j] - bit[st][j] == i - st + 1){
                            a = a*2 + 1;
                        }else{
                            a = a*2;
                        }
                    }
                    // cout<<a<<" * \n";
                    if(a > k){
                        ans = min(ans, abs(a - k));
                        break;
                    }else if(a == k)return 0;
                    ans = min(ans,abs(a - k));
                    st++;
                }
            }
            ans = min(ans,abs(a - k));
            // cout<<a<<" "<<ans<<endl;
        }
        return ans;
    }
};
