class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        map<int,set<int>> um;
        int n=nums.size();

        for(int i=0;i<n;i++){
            if(i==0){
               um[i].insert(nums[i]);
            }
            else{
                um[i].insert(nums[i]);
                for(auto e:um[i-1]){
                    um[i].insert(e&nums[i]);
                }

            }
        }

        int ans=1e9;
        for(auto e:um){
            set<int> s=e.second;
            auto it=s.lower_bound(k);
            if(it==s.end()){
                it--;
                ans=min(ans,abs(k-*it));
            }else{
                ans=min(ans,abs(k-*it));
                if(it!=s.begin()){
                    it--;
                    ans=min(ans,abs(k-*it));
                }
            }
        }
        return ans;
    }
};
