class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) 
    {
       int ans=1e9,n=nums.size();
       vector<int>v(32,0);
       vector<vector<int>>mp(n+1);
       mp[0]=v;
        for(int i=0;i<n;i++) 
        {
            int mid=i,st=0,end=i;
            for(int j=0;j<32;j++)
            {
                if(((nums[i]>>j)&1)==1) v[j]++;
            }
            mp[i+1]=v;
            ans=min(ans,abs(k-nums[i]));
            while(st<=end)
            {
                int mid=(st+end)/2;
                int val=0;
                 for(int j=0;j<32;j++) if(mp[i+1][j]-mp[mid][j]==i-mid+1) val+=pow(2,j);
                 ans=min(ans,abs(k-val));
                 if(val>k) end=mid-1;
                 else st=mid+1;
            }
        }
        return ans;
    }
};
