class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:

        @cache
        def dfs(currentIndex,currentVal):
            if(currentIndex==len(nums)):
                return abs(currentVal-k)
            currentVal=nums[currentIndex] & currentVal
            diff=min(abs(currentVal-k),dfs(currentIndex+1,currentVal))
            #print(currentIndex,currentVal,diff)
            return diff
        ans=sys.maxsize
        for i in range(len(nums)):
            ans=min(ans,dfs(i,nums[i]))
        return ans
