class Solution {
public:
    const static int N = 100000;
    int info[N + 1][31][2];
    const static int k = 17;
    const static int mxn = 100000;
    int sparse[k + 1][mxn + 1];

    void make_sparse(vector<int> &arr)
    {
        int n = arr.size();
        for (int i = 0; i < n; i++)
            sparse[0][i] = arr[i];

        for (int i = 1; i <= k; i++)
        {
            for (int j = 0; j + (1ll << i) <= n; j++)
            {
                sparse[i][j] = (sparse[i - 1][j] & sparse[i - 1][j + (1ll << (i - 1))]);
            }
        }
    }

    int query(int l, int r)
    {
        int ans = (1ll<<31) - 1;
        for (int i = k; i >= 0; i--)
        {
            if ((1ll << i) <= (r - l + 1))
            {
                ans = ans & sparse[i][l];
                l += (1ll << i);
            }
        }
        return ans;
    }
    int minimumDifference(vector<int>& arr, int k) {
        int n = arr.size();
        for (int i=n-1;i>=0;i--){
            for (int bit=30;bit>=0;bit--){
                info[i][bit][0] = n; info[i][bit][1] = -1;
                if ((1ll<<bit)&arr[i]){
                    info[i][bit][1] = i;
                    if (i + 1<n){
                        info[i][bit][1] = max(info[i][bit][1],info[i + 1][bit][1]);
                        info[i][bit][0] = info[i+1][bit][0]; 
                    }
                }else
                    info[i][bit][0] = i;
            }
        }
        make_sparse(arr);
        int ans = (1<<30);
        for (int i=0;i<n;i++){
            // try
            for (int bit=30;bit>=0;bit--){
                int zr = info[i][bit][0] ;
                if (zr - 1>= i)
                    ans = min(ans ,abs(k - query(i ,zr - 1)));
                if (zr < n)
                    ans = min(ans , abs(k - query(i  ,zr)));



            }
        }
        return ans;
    }
};
