class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n =nums.size();

        int i=0;
        int j=0;
        int curr=INT_MAX;
        int res=abs(nums[0]-k);
        vector<int> D(32, 0);
        while(j<=n&&i<n)
        {
           // cout<<i<<" "<<j<<" "<<curr<<"  "<<res<<endl;
            if(j<n&&curr>k)
            {
                curr=curr&nums[j];
                int p=0;
                int cp=nums[j];
                while(nums[j]>0)
                {
                    if(nums[j]%2==1)
                        D[p]++;
                    nums[j]/=2;
                    p++;
                }
                nums[j]=cp;
                j++;
                res=min(res, abs(curr-k));
            }
            else if(curr<k)
            {
                int p=0;
                int cp=nums[i];
                while(nums[i]>0)
                {
                    if(nums[i]%2==1)
                    {
                        D[p]--;
                    }
                    nums[i]/=2;
                    p++;
                }
                curr=0;
                for(int l=0; l<30;l++)
                {
                        if(D[l]==j-i-1)
                            curr+=pow(2, l);
                }
                nums[i]=cp;
                i++;
                if(i<j)
                res=min(res, abs(curr-k));
            }
            else
                break;

            if(i==j)
                curr=INT_MAX;
        }

      // cout<<i<<" "<<j<<" "<<curr<<endl;

        return res;

    }
};


