class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<int> ands;
        int ans = 1e9 + k;

        for (int x : nums) {
            unordered_set<int> nw = {x};
            for (int y : ands) nw.insert(x & y);
            ands = vector<int>(begin(nw), end(nw));
            for (int y : ands) ans = min(ans, abs(k - y));
        }
        return ans;
    }
};
