typedef long long ll ; 
//Implemented on 0 indexing
const ll N = 100001; //size of array
const ll k = 25;  //mx no of bits required to represent a query length

ll st[N][k+1];
ll n ;

ll a[N];

int logg[N+1];

ll combine(ll a, ll b) { return a&b; }

void build()
{
    for (ll i = 0; i < n; ++i)
        st[i][0] = a[i] ;

    for (ll j = 1; j <= k; ++j)
        for (ll i = 0; i + (1 << j) <= n; ++i)
            st[i][j] = combine(st[i][j - 1], st[i + (1 << (j - 1))][j - 1]);


    logg[1] = 0;
    for (int i = 2; i <= n; i++)
        logg[i] = logg[i/2] + 1;
}

ll query1(ll l , ll r)
{
    int j = logg[r - l + 1];
    return combine(st[l][j], st[r - (1 << j) + 1][j]);
}

class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        for(int i = 0  ;i<nums.size();++i)
            a[i] = nums[i] ; 
        n = nums.size();

        build();

        ll ans = 1e18 ; 

        for(ll i = 0 ;i<nums.size();++i)
        {
            ans = min(ans , ll(abs(nums[i]-k))) ; 
        }

        for(ll i = 0 ; i<nums.size();++i)
        {
            ll l = i, h = n-1 ; 
            while(l<=h)
            {
                ll m = (l+h)/2 ; 
                ll val = query1(i,m);

                if(val>=k)
                {
                    ans = min(ans,abs(val-k));
                    l = m+1 ; 
                }
                else 
                {
                    h = m-1 ; 
                }

            }
            l = i , h = n-1 ; 
             while(l<=h)
            {
                ll m = (l+h)/2 ; 
                ll val = query1(i,m);

                if(val<=k)
                {
                    ans = min(ans,abs(val-k));
                    h = m-1 ; 
                }
                else 
                {
                   l = m+1 ; 
                }
            }
        }
        return ans ; 

    }
};
