class Solution {
private:
    void minusZeros(vector<int>& zeros, int x) {
        for (int i = 0; i < 32; ++i) {
            if (((x >> i) & 1) == 0) {
                zeros[i] -= 1;
            }
        }
    }

    void plusZeros(vector<int>& zeros, int x) {
        for (int i = 0; i < 32; ++i) {
            if (((x >> i) & 1) == 0) {
                zeros[i] += 1;
            }
        }
    }

    int evaluate(vector<int>& zeros) {
        int res = 0;
        for (int i = 0; i < 32; ++i) {
            if (zeros[i] == 0) {
                res ^= (1 << i);
            }
        }
        return res;
    }
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<int> zeros(32, 0);

        int i = 0;
        int n = nums.size();
        int ans = INT_MAX;

        for(int j = 0; j < n; j++){
            plusZeros(zeros, nums[j]);
            int x = evaluate(zeros);
            ans = min(ans, abs(x - k));
            while(i < j && x < k){
                minusZeros(zeros, nums[i]);
                i++;
                x = evaluate(zeros);
                ans = min(ans, abs(x - k));
            }
        }

        return ans;
    }
};
