class Solution {
    public int[] zeros = new int[32];

    public void solve(int x,int op) { for (int i = 0; i < 32; ++i)  if (((x >> i) & 1) == 0)  zeros[i] += op*1; }

    public int cal() { int res = 0; for (int i = 0; i < 32; ++i)  if (zeros[i] == 0)  res ^= (1 << i); return res; }

    public int minimumDifference(int[] nums, int k) {
        int output = Integer.MAX_VALUE,top = 0;
        for (int bot = 0; bot < nums.length; ++bot) {
            solve(nums[bot],1);
            int x = cal();
            output = Math.min(output, Math.abs(x - k));
            while (top < bot && x < k) {
                solve(nums[top],-1);
                ++top;
                x = cal();
                output = Math.min(output, Math.abs(x - k));
            }
        }

        return output;
    }
}
