class SegTree{
    private:
    int n;
    vector<int> tr;
    public:
    SegTree(int N){
        n = N;
        tr.resize(4*n+1,0);
    }
    void update(int ind,int val,int low,int high,int i){
        if(low == high){
            tr[ind] = val;
            return;
        }
        int mid = (low+high)/2;
        if(i<=mid)  update(2*ind+1,val,low,mid,i);
        else    update(2*ind+2,val,mid+1,high,i);
        tr[ind] = (tr[2*ind+1]&tr[2*ind+2]);
    }
    int query(int ind,int low,int high,int l,int r){
        // low high l r     l r low high
        if(high < l || r < low) return INT_MAX;
        // l low high r
        if(low >=l && high <= r)    return tr[ind];
        int mid = (low+high)/2;
        return (query(2*ind+1,low,mid,l,r)&query(2*ind+2,mid+1,high,l,r));
    }
};
class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        int ans = INT_MAX;
        SegTree tr(n);
        for(int i=0;i<n;i++){
            ans = min(ans,abs(nums[i]-k));
            tr.update(0,nums[i],0,n-1,i);
        }
        for(int i=0;i<n;i++){
            if(nums[i] <= k){
                continue;
            }
            int l = i,u = n-1;
            while(l <= u){
                int mid = (l+u)/2;
                int ad = tr.query(0,0,n-1,i,mid);
                if(ad >= k){
                    l = mid+1;
                    ans = min(ans,abs(ad-k));
                }
                else{
                    u = mid-1;
                }
            }
            l = i,u = n-1;
            while(l<=u){
                int mid = (l+u)/2;
                int ad = tr.query(0,0,n-1,i,mid);
                if(ad < k){
                    u = mid-1;
                    ans = min(ans,abs(ad-k));
                }
                else{
                    l = mid+1;
                }
            }
        }
        return ans;
    }
};
