class CustomSegmentTree {
private:
    std::vector<int> tree;
    int size;

    void construct(const std::vector<int>& arr, int node, int start, int end) {
        if (start == end) {
            tree[node] = arr[start];
        } else {
            int mid = (start + end) / 2;
            construct(arr, 2 * node + 1, start, mid);
            construct(arr, 2 * node + 2, mid + 1, end);
            tree[node] = tree[2 * node + 1] & tree[2 * node + 2];
        }
    }

    int range_and_query(int node, int start, int end, int left, int right) {
        if (right < start || end < left) {
            return INT_MAX; // This effectively acts as a bitmask with all 1s (i.e., no effect on AND operation)
        }
        if (left <= start && end <= right) {
            return tree[node];
        }
        int mid = (start + end) / 2;
        int left_result = range_and_query(2 * node + 1, start, mid, left, right);
        int right_result = range_and_query(2 * node + 2, mid + 1, end, left, right);
        return left_result & right_result;
    }

public:
    CustomSegmentTree(const std::vector<int>& arr) {
        size = arr.size();
        tree.resize(4 * size, 0);
        construct(arr, 0, 0, size - 1);
    }

    int query(int left, int right) {
        return range_and_query(0, 0, size - 1, left, right);
    }
};

class Solution {
public:
    int minimumDifference(std::vector<int>& nums, int k) {
        int size = nums.size();
        CustomSegmentTree segment_tree(nums);
        int min_difference = INT_MAX;

        for (int i = 0; i < size; ++i) {
            int left = i - 1;
            int right = size - 1;
            while (right - left > 1) {
                int mid = (left + right) / 2;
                if (segment_tree.query(i, mid) >= k) {
                    left = mid;
                } else {
                    right = mid;
                }
            }
            if (left != i - 1) {
                min_difference = std::min(min_difference, std::abs(segment_tree.query(i, left) - k));
            }
            min_difference = std::min(min_difference, std::abs(segment_tree.query(i, right) - k));
        }

        return min_difference;
    }
};
