class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<int>bits(32);
        int st=0;
        int ans=INT_MAX;
        for(int i=0; i<nums.size(); i++)
        {
            int v=nums[i];
            for(int j=0; j<30; j++)
            {
                if(v%2!=1)
                bits[j]++;
                v/=2;
            }
            while(st<i)
            {
                vector<int>nb=bits;
                v=nums[st];
                for(int j=0; j<30; j++)
                {
                    if(v%2!=1)
                    nb[j]--;
                    v/=2;
                }
                if(abs(count(nb)-k)<=abs(count(bits)-k))
                {
                    bits=nb;
                    st++;
                }
                else
                {
                    break;
                }
            }
            ans=min(ans,abs(count(bits)-k));

        }
        return ans;
    }
    int count(vector<int>&v)
    {
        int ans=0;
        int c=1;
        for(int i=0; i<30; i++)
        {
            if(v[i]==0)
            {
                ans+=c;
            }
            c*=2;
        }
        return ans;
    }
};
