class Solution {
public:
    int minanswer(vector<int>& nums,int k){
        int n=nums.size();
        vector<vector<int> > v1(n+5,vector<int>(32,-1));
        for(int i=0;i<nums.size();i++){
            int x=nums[i];
            for(int j=0;j<=30;j++){
                if(!(x&(1<<j))){
                    v1[i][j]=i;
                }
            }
        }

        for(int i=nums.size()-2;i>=0;i--){
            for(int j=0;j<=30;j++){
                if(v1[i][j]==-1){
                    v1[i][j]=v1[i+1][j];
                }
            }
        }

        int maxanswer=1000000000;
        for(int i=0;i<n;i++){
            int x=nums[i];
            maxanswer=min(maxanswer,abs(k-x));
            int index=i;
            while(index<n&&x>0){
                int y=1000000;
                for(int j=0;j<=30;j++){
                    if(v1[index][j]!=-1&&((x&(1<<j))>0)){
                        y=min(v1[index][j],y);
                    }
                }
                if(y>n)
                    break;
                for(int j=0;j<=30;j++){
                    if(v1[y][j]==y&&((x&(1<<j))>0)){
                        x^=(1<<j);
                    }
                }
                maxanswer=min(maxanswer,abs(k-x));
                index=y+1;
            }
        }
        return maxanswer;        
    }
    int minimumDifference(vector<int>& nums, int k) {
        int x=minanswer(nums,k);
        reverse(nums.begin(),nums.end());
        int y=minanswer(nums,k);

        return min(x,y);
    }
};
