        for(int q = 0; q < 32; q++){
            if((1<<q)&nums[0])
                cnt[q]++;
        }
        res = min(res, abs(getCur(cnt)-k));
        while(i < n){
            if(getCur(cnt) > k || j > i){
                i++;
                added++;
                if(i >= n) break;
                for(int q = 0; q < 32; q++){
                    if((1<<q)&nums[i])
                        cnt[q]++;
                }
                //cout << getCur(cnt) << nums[i] << endl;
            }else{
                added--;
                for(int q = 0; q < 32; q++){
                    if((1<<q)&nums[j])
                        cnt[q]--;
                }
                j++;
            }
            if(j <= i){

                int cur = getCur(cnt);
                res = min(res, abs(cur-k));
            }
        }
        return res;
    }
