class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        if k in nums:
            return 0

        if k > max(nums):
            return k - max(nums)

        ans = 10**12

        for ea in range(len(nums)):
            base = nums[ea]

            ans = ans if ans < abs(base - k) else abs(base - k)
            for jh in range(ea + 1, len(nums)):
                base &= nums[jh]
                ans = ans if ans < abs(base - k) else abs(base - k)
                if base <= k:
                    break
            if base > k:
                break


        return ans
