class SegmentTree {
    private int[] tree;
    private int size;

    public SegmentTree(int[] data) {
        this.size = data.length;
        this.tree = new int[4 * size];
        build(data, 0, 0, size - 1);
    }

    private void build(int[] data, int node, int start, int end) {
        if (start == end) {
            tree[node] = data[start];
        } else {
            int mid = (start + end) / 2;
            build(data, 2 * node + 1, start, mid);
            build(data, 2 * node + 2, mid + 1, end);
            tree[node] = tree[2 * node + 1] & tree[2 * node + 2];
        }
    }

    public int query(int left, int right) {
        return queryInternal(0, 0, size - 1, left, right);
    }

    private int queryInternal(int node, int start, int end, int left, int right) {
        if (right < start || left > end) {
            return Integer.MAX_VALUE;
        }
        if (left <= start && end <= right) {
            return tree[node];
        }
        int mid = (start + end) / 2;
        int leftResult = queryInternal(2 * node + 1, start, mid, left, right);
        int rightResult = queryInternal(2 * node + 2, mid + 1, end, left, right);
        return leftResult & rightResult;
    }
}

class Solution {
    public int minimumDifference(int[] nums, int k) {
        int n = nums.length;
        SegmentTree st = new SegmentTree(nums);
        int ans = Integer.MAX_VALUE;

        for (int i = 0; i < n; i++) {
            int l = i - 1, r = n - 1;
            while (r - l > 1) {
                int mid = (l + r) / 2;
                if (st.query(i, mid) >= k) {
                    l = mid;
                } else {
                    r = mid;
                }
            }
            if (l != i - 1) {
                ans = Math.min(ans, Math.abs(st.query(i, l) - k));
            }
            ans = Math.min(ans, Math.abs(k - st.query(i, r)));
        }
        return ans;
    }
}
