class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        ans = abs(nums[0] - k)
        valid = {nums[0]}
        for num in nums:
            valid = {x & num for x in valid} | {num}
            ans = min(ans, min(abs(x - k) for x in valid))
        return ans
