class Solution {
    public int minimumDifference(int[] nums, int k) {
        int n = nums.length;
        int a = 0;
        int b = 0;
        int[] ct = new int[30];
        int curr = Integer.MAX_VALUE;
        int ans = Integer.MAX_VALUE;
        while (a < n) {
            if (curr >= k && b < n) {
                curr = 0;
                for (int i = 0; i < 30; ++i) {
                    if (((1<<i)&nums[b])==0)
                        ++ct[i];
                    if (ct[i]==0)
                        curr+=1<<i;
                }
                ++b;
            } else {
                curr = 0;
                for (int i = 0; i < 30; ++i) {
                    if (((1<<i)&nums[a])==0)
                        --ct[i];
                    if (ct[i]==0)
                        curr+=1<<i;
                }
                ++a;
            }
            //System.out.println(a+" "+b+" "+curr);
            ans = Math.min(ans,Math.abs(k-curr));
        }
        curr = Integer.MAX_VALUE;
        a = 0;
        b = 0;
        ct = new int[30];
        while (a < n) {
            if (curr > k && b < n) {
                curr = 0;
                for (int i = 0; i < 30; ++i) {
                    if (((1<<i)&nums[n-b-1])==0)
                        ++ct[i];
                    if (ct[i]==0)
                        curr+=1<<i;
                }
                ++b;
            } else {
                curr = 0;
                for (int i = 0; i < 30; ++i) {
                    if (((1<<i)&nums[n-a-1])==0)
                        --ct[i];
                    if (ct[i]==0)
                        curr+=1<<i;
                }
                ++a;
            }
            //System.out.println((n-a-1)+" "+(n-b-1)+" "+curr);
            ans = Math.min(ans,Math.abs(k-curr));
        }
        return ans;
    }
}
