class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        vector<unordered_set<int>> v(n);

        v[n - 1] = { nums.back() };

        for (int i = n - 2; i >= 0; i--) {
            v[i].insert(nums[i]);
            for (int x : v[i + 1]) {
                v[i].insert(nums[i] & x);
            }
        }

        int ans = 2e9;
        for (auto& s : v) {
            for (int x : s) ans = min(ans, abs(k - x));
        }
        return ans;
    }
};
