class Solution {
public:
    void add(vector<int64_t>& counter, int64_t x) {
        int k = 0;
        while (x)
        {
            int64_t new_x = x & counter[k];
            counter[k] = x ^ counter[k];
            x = new_x;
            k += 1;
        }
    }

    void sub(vector<int64_t>& counter, int64_t x)
    {
        int k = 0;
        while (x)
        {
            int64_t new_x = x & ~counter[k];
            counter[k] = x ^ counter[k];
            x = new_x;
            k += 1;
        }
    }

    int64_t val(vector<int64_t>& counter, int64_t count)
    {
        int k = 0;
        int64_t res = -1;
        while (count) {
            if (count % 2 > 0) {
                res &= counter[k];
            } else {
                res &= ~counter[k];
            }
            count /= 2;
            k += 1;
        }
        return res;
    }

    int64_t solve(vector<int>& A, int P) {
        vector<int64_t> counter(32, 0);
        int n = A.size();
        int i = 0, j = 0;
        int64_t K = P;
        int64_t mini = LLONG_MAX;
        while (i < n)
        {
            if (K < P || j == n)
            {
                sub(counter, A[i]);
                i += 1;
            }
            else
            {
                add(counter, A[j]);
                j += 1;
            }

            if (j > i)
            {
                K = val(counter, j - i);
                int64_t X = abs(K - P);
                if (mini > X)
                {
                    mini = X;
                }
            }
            else
            {
                K = P;
            }
        }

        return mini;
    }
