class Solution {
public:
    deque<int> d;
    int cnt[30], val;

    void calVal() {
        val = 0;
        for (int i = 0; i < 30; i++) {
            if (cnt[i] == d.size()) val |= 1 << i;
        }
    }

    void add(int x) {
        d.push_back(x);
        for (int i = 0; i < 30; i++) {
            if ((x >> i) & 1) ++ cnt[i];
        }
        calVal();
    }

    void rem() {
        int x = d.front();
        d.pop_front();
        for (int i = 0; i < 30; i++) {
            if ((x >> i) & 1) -- cnt[i];
        }
        calVal();
    }

    int minimumDifference(vector<int>& nums, int k) {
        val = 1e9 + 10;
        for (int i = 0; i < 30; i++) cnt[i] = 0;
        d.clear();

        int n = nums.size();
        int ri = 0, ans = 1e9 + 10;
        for (int i = 0; i < n; i++) {
            while (ri < n && val > k) {
                add(nums[ri++]);
                ans = min(ans, abs(k - val));
            }
            rem();
            if (!d.empty()) ans = min(ans, abs(k - val));
        }
        return ans;
    }
};
