class Solution {
public:
    vector<vector<int>>ct;
    // Min value greater than k
    int solve(vector<int>& nums, int pos, int k){
        int n = nums.size();
        int l = pos, r = n-1;
        int ret = 2e9;
        while(l<=r){
            int mid = (l+r)/2;
            int val = 0;
            for(int i=0;i<32;i++){
                if(ct[mid+1][i] - ct[pos][i] == mid - pos + 1)val += (1<<i);
            }
            if(val >= k)ret = min(ret, val), l = mid+1;
            else r = mid-1;
        }
        if(ret == 2e9)return -1;
        return ret;
    }
    // Max value less than k
    int solve2(vector<int>& nums, int pos, int k){
        int n = nums.size();
        int l = pos, r = n-1;
        int ret = -1;
        while(l<=r){
            int mid = (l+r)/2;
            int val = 0;
            for(int i=0;i<32;i++){
                if(ct[mid+1][i] - ct[pos][i] == mid - pos + 1)val += (1<<i);
            }
            if(val <= k)ret = max(ret, val), r = mid-1;
            else l = mid+1;
        }
        return ret;
    }
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        ct.assign(n+1, vector<int>(32,0));
        for(int i=0;i<n;i++){
            ct[i+1] = ct[i];
            for(int j=0;j<32;j++){
                if(nums[i] & (1<<j))ct[i+1][j]++;
            }
        }

        int ret = 2e9;
        for(int i=0;i<n;i++){
            int x = solve(nums, i, k), y = solve2(nums, i, k);

            if(x != -1)ret = min(ret, abs(x-k));
            if(y != -1)ret = min(ret, abs(y-k));
        }
        return ret;

    }
};
