class Solution {
public:
    int minimumDifference(vector<int>& v, int k) {
        int i = 0, j = 0, n = v.size(), curr = INT_MAX, ans = INT_MAX;
        vector<int> cnt(32, 0);
        while(j < n){
            ans = min(ans, abs(v[j] - k));
            for(int bit = 0; bit < 31; bit++){
                if(((1 << bit) & v[j]) == 0)
                    cnt[bit]++;
            }
            curr = 0;
            for(int bit = 0; bit < 31; bit++){
                if(!cnt[bit]){
                    curr |= (1 << bit);
                }
            }
            ans = min(ans, abs(curr - k));
            j++;
            while(curr < k && i < j){
                for(int bit = 0; bit < 31; bit++){
                if(((1 << bit) & v[i]) == 0)
                    cnt[bit]--;
                }
                curr = 0;
                i++;
                for(int bit = 0; bit < 31; bit++){
                    if(!cnt[bit]){
                        curr |= (1 << bit);
                    }
                }
                ans = min(ans, abs(curr - k));
            }
            ans = min(ans, abs(curr - k));
        }
        while(i < j){
            for(int bit = 0; bit < 31; bit++){
                if(((1 << bit) & v[i]) == 0)
                    cnt[bit]--;
            }
            curr = 0;
            i++;
            for(int bit = 0; bit < 31; bit++){
                if(!cnt[bit]){
                    curr |= (1 << bit);
                }
            }
            ans = min(ans, abs(curr - k));
        }
        return ans;
    }
};
