class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        @cache
        def dp(x, y):
            if x == len(nums):
                if y is None:
                    return math.inf
                else:
                    return abs(y - k)

            ans = math.inf
            if y is not None:
                ans = min(ans, abs(y - k))
                ans = min(ans, dp(x + 1, y & nums[x]))
            else:
                ans = min(ans, dp(x + 1, nums[x]))
                ans = min(ans, dp(x + 1, None))
            return ans

        return dp(0, None)
