class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<int> ct(32,0);
        int n=nums.size();
        int l=0;
        int ans=1e9;
        for(int i=0;i<n;i++){
            for(int j=0;j<30;j++){
                if((1<<j)&nums[i])ct[j]++;
            }
            while(l<=i){
                int t=0;
                for(int j=0;j<30;j++){
                    if(i-l+1==ct[j])t+=(1<<j);
                }
                ans=min(ans,abs(t-k));
                if(t<=k){
                    for(int j=0;j<30;j++){
                        if((1<<j)&nums[l])ct[j]--;
                    }
                    l++;
                }else break;
            }
        }
        return ans;
    }
};
